import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";

const socket = io("http://localhost:5000"); // Replace with your server URL

interface Message {
  username: string;
  message: string;
}

export const ChatBox: React.FC = () => {
  const { username } = useParams<{ username: string }>(); // Extract username from URL params

  const [messages, setMessages] = useState<Message[]>([]);
  const [messageInput, setMessageInput] = useState("");

  useEffect(() => {
    // Event listener for incoming messages
    socket.on("message", (data: Message) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    return () => {
      socket.disconnect(); // Disconnect socket when component unmounts
    };
  }, []);

  const sendMessage = () => {
    socket.emit("message", { username, message: messageInput });
    setMessageInput(""); // Clear input field after sending message
  };

  return (
    <div className="flex items-center justify-center">
        <div className="h-full overflow-y-auto p-4">
          {messages.map((msg, index) => (
            <div key={index} className="mb-2">
              <strong>{msg.username}:</strong> {msg.message}
            </div>
          ))}
        </div>
        <div className="flex items-center border-t border-gray-300 p-2">
          <input
            type="text"
            value={messageInput}
            onChange={(e) => setMessageInput(e.target.value)}
            placeholder="Type your message..."
            className="flex-1 px-2 py-1 border rounded-md mr-2"
          />
          <button
            onClick={sendMessage}
            className="px-4 py-1 bg-blue-500 text-white rounded-md hover:bg-blue-600"
          >
            Send
          </button>
        </div>

    </div>
  );
};
